// import logo from './logo.svg';
import "./App.css";
import Navbar from "./components/Navbar";
import Hero from "./components/Hero";
import Option from "./components/Option";
import Menu from "./components/Menu";
import Bags from "./components/Bags";
import Shoes from "./components/Shoes";
import Contact from "./components/Contact";
import Footer from "./components/Footer";

// Icon
import { MdOutlineArrowBackIosNew } from "react-icons/md";
import { MdOutlineArrowForwardIos } from "react-icons/md";
import styled from "styled-components";
import { BagsData, MenuData, OptionData, ShoeData } from "./constant";

// Circle arrow Icon styled-component
const Circle = styled.div`
  background-color: #ececec;
  width: 3rem;
  height: 3rem;
  margin-top: 2rem;
  cursor: pointer;
  border-radius: 50%;
  position: relative;
`;

function App() {
  return (
    <div className='App'>
      {/* Navbar */}
      <div className='bg-[#FFF9F3]'>
        <Navbar />
      </div>

      {/* Hero */}
      <Hero />

      {/* Shoes */}
      <Shoes />

      {/* options */}
      <div className='flex w-[94%] max-w-7xl m-auto gap-16 mb-[2%]'>
        {OptionData.map((item) => {
          return <Option data={item} />;
        })}
      </div> 

      <div className='w-[94%] max-w-7xl m-auto'>
        <h1 className='text-black text-[21px] font-semibold max-lg:text-[20.5px]'>
          Find things you'll love. Support independent sellers.
        </h1>
        <p className='text-slate-500 text-[16px] mb-5 max-lg:text-[15px]'>Only on polka.</p>
      </div>

      <div className='flex ml-[16%] max-lg:ml-[3%] max-lg:mr-0 max-lg:space-x-0 max-lg:gap-4'>
        <Circle className=' hover:bg-[#dad8d8] max-lg:hidden'>
          <MdOutlineArrowBackIosNew className='text-black absolute top-3.5 left-3.5 text-xl cursor-pointer' />
        </Circle>
        {MenuData.map((images) => {
          return <Menu Data={images} />;
        })}
        <Circle className='mb-3 hover:bg-[#dad8d8] max-lg:hidden'>
          <MdOutlineArrowForwardIos className='text-black absolute top-3.5 left-3.5 text-xl cursor-pointer' />
        </Circle>
      </div>


      <div className="w-[94%] max-w-7xl m-auto mt-8 mb-8">
        <h1 className='text-black text-[21px] font-semibold max-lg:text-[20.5px]'>
          Discover unique hand-picked items
        </h1>
        <div className='flex space-x-4 max-lg:mt-[3%] max-lg:mr-0 max-lg:space-x-0 max-lg:gap-4'>
        <Circle className=' hover:bg-[#dad8d8] max-lg:hidden'>
          <MdOutlineArrowBackIosNew className='text-black absolute top-3.5 left-3.5 text-xl cursor-pointer' />
        </Circle>
          {BagsData.map((items) => {
            return <Bags Data={items} />;
          })}
            <Circle className='mb-3 hover:bg-[#dad8d8] max-lg:hidden'>
          <MdOutlineArrowForwardIos className='text-black absolute top-3.5 left-3.5 text-xl cursor-pointer' />
        </Circle>
        </div>
      </div>

      {/*       
      <div className='flex space-x-14 ml-[19.8rem] mb-10 mt-14 max-lg:ml-[2%] max-lg:mr-[2%] max-lg:space-x-8'>
        {ShoeData.map((img) => {
          return <Shoes Data={img} />;
        })}
      </div> */}

      {/* <div className='flex items-center space-x-[2.6rem] mt-[6rem] ml-[20rem] max-lg:ml-[2%] max-lg:mr-0 max-lg:space-x-5'>
        {BagsData.map((items) => {
          return <Bags Data={items} />;
        })}
      </div> */}

      {/* <Contact /> */}
      {/*   */}

      {/* <Footer /> */}
    </div>
  );
}

export default App;
