import styled from "styled-components";
import { BiChevronDown } from "react-icons/bi";
import { CiSearch } from "react-icons/ci";
import { RxPerson } from "react-icons/rx";
import { BiShoppingBag } from "react-icons/bi";
import { useState } from "react";
import React from "react";
  const Btn = styled.button`
    background-color: #eb6d20;
    color: #fff;
    font-size: 15px;
    padding: 5px 20px 5px 20px;
    border-radius: 20px;
    width: 4.5rem;
    height: 2rem;
    @media (max-width: 1024px) {
      padding: 0 18px 0 18px;
      margin-left: 10rem;
    }
    `;

const Navbar = () => {

  const [showNav, setShowNav] = useState(false);

    const handleClick = () => {
      setShowNav(!showNav);
      console.log(showNav)
    }
  return (
    <nav className=' w-[94%] max-w-7xl m-auto'>
      <div className='flex pt-5 space-x-4 items-center max-lg:space-x-4'>
        <img
          className=''
          src='/images/Logo.png'
          alt='logo'
        />

        <div className='bg-white flex w-[55%] rounded-3xl'>
          <div className='bg-[#F5F6F8] px-3 pl-5 text-base font-normal rounded-3xl flex items-center gap-1 w-[12rem] cursor-pointer hover:bg-[#dddddd] max-lg:w-[34%] max-lg:text-[14.5px] max-lg:px-3 max-lg:ml-1'>
            All categories
            <BiChevronDown className='text-xl cursor-pointer' />
          </div>
          <input
            type='text'
            name='Search'
            placeholder='Search anything'
            className='text-center w-[40rem] ml-4 rounded-3xl max-lg:w-[17rem]'
          />
          <CiSearch className='text-3xl cursor-pointer max-lg:text-3xl max-lg:pt-1  max-lg:pl-1' />
          </div>

        {/* <div className="flex space-x-5 max-lg:order-1 max-lg:place-self-end"> */}
        <Btn className='hover:bg-[#d96117] '>Help</Btn>
        <div className='flex items-center gap-2 text-[15.9px] font-[500] cursor-pointer hover:underline max-lg:gap-1 max-lg:pl-2 max-lg:pr-5'>
          <RxPerson className='text-lg hover:text-red-400 cursor-pointer' />
          Account
        </div>
        <div className='flex items-center font-[500] hover:text-[#d9] gap-2 text-[15.9px] cursor-pointer hover:underline  max-lg:pl-0'>
          <BiShoppingBag className='text-lg hover:text-red-400 cursor-pointer' />
          Shopping
        </div>
        </div>


      {/* </div> */}

      {/* lineTwo */}
      <div className="hidden max-md:block" onClick={handleClick}>Click</div>
      <div className={`max-lg:absolute max-lg:top-[18%] max-lg:z-10 max-lg:transition-all max-lg:duration-300 max-lg:ease-in max-lg:w-0 max-lg:bg-[#FFF9F3] max-lg:overflow-hidden max-lg:h-[50%] ${showNav && "max-lg:w-[50%] max-lg:z-10"}`}>
        <ul className='flex text-[15px] space-x-16 pt-5 font-[500] max-lg:flex-col max-xl:space-x-10 max-2xl:space-x-16'>
          <li className='pb-2 border-[#FFF9F3] border-b-2 cursor-pointer hover:text-[#eb6d20] hover:border-b-2 hover:border-b-[#eb6d20]'>
            Jewelry & Accessories
          </li>
          <li className='pb-2 border-[#FFF9F3] border-b-2 cursor-pointer hover:text-[#eb6d20] hover:border-b-2 hover:border-b-[#eb6d20]'>
            Clothing & Shoes
          </li>
          <li className='pb-2 border-[#FFF9F3] border-b-2 cursor-pointer hover:text-[#eb6d20] hover:border-b-2 hover:border-b-[#eb6d20]'>
            Home & Living
          </li>
          <li className='pb-2 border-[#FFF9F3] border-b-2 cursor-pointer hover:text-[#eb6d20] hover:border-b-2 hover:border-b-[#eb6d20]'>
            Wedding & Party
          </li>
          <li className='pb-2 border-[#FFF9F3] border-b-2 cursor-pointer hover:text-[#eb6d20] hover:border-b-2 hover:border-b-[#eb6d20]'>
            Toys & Entertainment
          </li>
          <li className='pb-2 border-[#FFF9F3] border-b-2 cursor-pointer hover:text-[#eb6d20] hover:border-b-2 hover:border-b-[#eb6d20]'>
            Art & Collectibles
          </li>
          <li className='pb-2 border-[#FFF9F3] border-b-2 cursor-pointer hover:text-[#eb6d20] hover:border-b-2 hover:border-b-[#eb6d20]'>
            Craft Supplies & Tools
          </li>
        </ul>
      </div>
    </nav>
  );
};

export default Navbar;
